from extra_code import drive_base_reset, setup_hub
from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Button, Color, Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor, Remote
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
HUB = PrimeHub(top_side=Axis.Y, front_side=Axis.Z, broadcast_channel=1, observe_channels=[2])
Left_Color_Sensor = ColorSensor(Port.E)
Right_Color_Sensor = ColorSensor(Port.F)
Left_motor = Motor(Port.A, Direction.COUNTERCLOCKWISE)
Right_Motor = Motor(Port.B, Direction.CLOCKWISE)
drive_base = DriveBase(Left_motor, Right_Motor, 88, 128)
Left_Attachment = Motor(Port.C, Direction.CLOCKWISE)
Right_Attachment = Motor(Port.D, Direction.CLOCKWISE)
remote = Remote()

# Initialize variables.
Current_Program = 1
RC_Straight_Speed = 200
RC_Turn_Speed = 100
RC_attachment_speed = 300
RC_Straight = 0
RC_Turn = 0
RC_Left = 0
RC_Right = 0

async def Circle():
    global Current_Program
    await wait(0)
    HUB.light.on(Color.YELLOW)
    drive_base.drive(-100, 0)
    await HUB.speaker.beep(500, 300)
    drive_base.stop()
    await wait(200)
    drive_base.use_gyro(True)
    await drive_base.straight(250)
    await drive_base.curve(200, 360)
    drive_base.use_gyro(False)
    await drive_base.straight(-240, Stop.COAST)
    Current_Program = Current_Program + 1

async def subtask():
    await setup_hub(HUB)
    drive_base.settings(straight_speed=300)
    drive_base.settings(straight_acceleration=300)
    drive_base.settings(turn_rate=300)
    drive_base.settings(turn_acceleration=300)

async def subtask2():
    global Current_Program
    while True:
        await wait(0)
        HUB.display.number(Current_Program)
        HUB.light.on(Color.GREEN)
        if Button.LEFT in HUB.buttons.pressed():
            Current_Program = Current_Program - 1
            await wait(200)
        elif Button.RIGHT in HUB.buttons.pressed():
            Current_Program = Current_Program + 1
            await wait(200)
        elif Button.CENTER in HUB.buttons.pressed():
            if Current_Program == 0:
                await remote_mode()
            elif Current_Program == 1:
                await Square()
            elif Current_Program == 2:
                await Triangle()
            elif Current_Program == 3:
                await Circle()
            elif Current_Program == 4:
                await Example()
            elif Current_Program == 5:
                pass
            else:
                pass
        else:
            await wait(5)

async def Square():
    global Current_Program
    await wait(0)
    HUB.light.on(Color.YELLOW)
    drive_base.drive(-100, 0)
    await HUB.speaker.beep(500, 300)
    drive_base.stop()
    await wait(200)
    drive_base.use_gyro(True)
    await drive_base.straight(150)
    for count in range(4):
        await wait(0)
        await drive_base.straight(300)
        await drive_base.turn(90)
    drive_base.use_gyro(False)
    await drive_base.straight(-140, Stop.COAST)
    Current_Program = Current_Program + 1

async def remote_mode():
    global RC_Straight, RC_Turn, RC_Left, RC_Right
    await wait(0)
    while True:
        await wait(0)
        HUB.light.on(Color.RED)
        RC_Straight = 0
        RC_Turn = 0
        RC_Left = 0
        RC_Right = 0
        if Button.CENTER in remote.buttons.pressed():
            print(drive_base.distance(), ' mm driven')
            print(drive_base.angle(), ' degrees turned')
            print(Left_Attachment.angle(), ' degrees left attachment')
            print(Left_Attachment.angle(), ' degrees right attachment')
            Left_Attachment.reset_angle(0)
            Right_Attachment.reset_angle(0)
            await drive_base_reset(drive_base)
            await HUB.speaker.beep(500, 400)
        elif (Button.LEFT in remote.buttons.pressed()) and (Button.RIGHT_PLUS in remote.buttons.pressed()):
            RC_Right = RC_attachment_speed
        elif (Button.LEFT in remote.buttons.pressed()) and (Button.RIGHT_MINUS in remote.buttons.pressed()):
            RC_Right = -RC_attachment_speed
        elif (Button.RIGHT in remote.buttons.pressed()) and (Button.LEFT_PLUS in remote.buttons.pressed()):
            RC_Left = RC_attachment_speed
        elif (Button.RIGHT in remote.buttons.pressed()) and (Button.LEFT_MINUS in remote.buttons.pressed()):
            RC_Left = -RC_attachment_speed
        else:
            if Button.LEFT_PLUS in remote.buttons.pressed():
                RC_Straight = RC_Straight_Speed
            if Button.LEFT_MINUS in remote.buttons.pressed():
                RC_Straight = -RC_Straight_Speed
            if Button.RIGHT_PLUS in remote.buttons.pressed():
                RC_Turn = RC_Turn_Speed
            if Button.RIGHT_MINUS in remote.buttons.pressed():
                RC_Turn = -RC_Turn_Speed
        drive_base.drive(RC_Straight, RC_Turn)
        Left_Attachment.run(RC_Left)
        Right_Attachment.run(RC_Right)

async def Triangle():
    global Current_Program
    await wait(0)
    HUB.light.on(Color.YELLOW)
    drive_base.drive(-100, 0)
    await HUB.speaker.beep(500, 300)
    drive_base.stop()
    await wait(200)
    drive_base.use_gyro(True)
    await drive_base.straight(150)
    for count2 in range(3):
        await wait(0)
        await drive_base.straight(300)
        await drive_base.turn(120)
    drive_base.use_gyro(False)
    await drive_base.straight(-140, Stop.COAST)
    Current_Program = Current_Program + 1

async def Example():
    await wait(0)
    HUB.light.on(Color.YELLOW)
    drive_base.drive(-100, 0)
    await HUB.speaker.beep(500, 300)
    drive_base.stop()
    await wait(200)
    drive_base.use_gyro(True)
    await drive_base.straight(150)
    await remote_mode()

async def main():
    global Current_Program
    await multitask(subtask(), subtask2())


run_task(main())