from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Button, Color, Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
prime_hub = PrimeHub(top_side=-Axis.X, front_side=Axis.Z, broadcast_channel=0, observe_channels=[1])
Left_Color_Sensor = ColorSensor(Port.E)
Right_Color_Sensor = ColorSensor(Port.F)
Left_motor = Motor(Port.A, Direction.COUNTERCLOCKWISE)
Right_Motor = Motor(Port.B, Direction.CLOCKWISE)
drive_base = DriveBase(Left_motor, Right_Motor, 88, 128)
Left_Attachment = Motor(Port.C, Direction.CLOCKWISE)
Right_Attachment = Motor(Port.D, Direction.CLOCKWISE)

# Initialize variables.
Current_Program = 1
Straight_Speed = 500
Straight_Acceleration = 300
Turn_Speed = 200
Turn_Acceleration = 300
Attachment_Speed = 810

def Circle():
    global Current_Program
    # Turn button yellow; and beep/wait so hands are out of the way
    prime_hub.light.on(Color.YELLOW)
    prime_hub.speaker.beep(500, 300)
    # Turn on Gyro, drive forward and drive in a circle
    drive_base.use_gyro(True)
    drive_base.straight(250)
    drive_base.curve(200, 360)
    # Turn off Gyro and drive/coast back home
    drive_base.use_gyro(False)
    drive_base.straight(-240, Stop.COAST)
    # Advance to next program
    Current_Program = Current_Program + 1

def Square():
    global Current_Program
    # Turn button yellow; and beep/wait so hands are out of the way
    prime_hub.light.on(Color.YELLOW)
    prime_hub.speaker.beep(500, 300)
    # Turn on Gyro, drive forward and drive in a square
    drive_base.use_gyro(True)
    drive_base.straight(150)
    for count in range(4):
        drive_base.straight(300)
        drive_base.turn(90)
    # Turn off Gyro and drive/coast back home
    drive_base.use_gyro(False)
    drive_base.straight(-140, Stop.COAST)
    # Advance to next program
    Current_Program = Current_Program + 1

def Triangle():
    global Current_Program
    # Turn button yellow; and beep/wait so hands are out of the way
    prime_hub.light.on(Color.YELLOW)
    prime_hub.speaker.beep(500, 300)
    # Turn on Gyro, drive forward and drive in a square
    drive_base.use_gyro(True)
    drive_base.straight(150)
    for count2 in range(3):
        drive_base.straight(300)
        drive_base.turn(120)
    # Turn off Gyro and drive back home
    drive_base.use_gyro(False)
    drive_base.straight(-140, Stop.COAST)
    # Advance to next program
    Current_Program = Current_Program + 1

def Example():
    # This is an example of an example of a start of an FLL program,
    #  in this example the robot initially drives backwards and squares up
    # against the wall before driving forward. Lining up and squaring one
    # the rear wall is a good strategy in FLL
    prime_hub.light.on(Color.YELLOW)
    drive_base.drive(-100, 0)
    prime_hub.speaker.beep(500, 300)
    drive_base.stop()
    wait(200)
    # Turn on Gyro after squaring off the back wall then
    #  drive forward and move left attachment
    drive_base.use_gyro(True)
    drive_base.straight(150)
    Left_Attachment.run_angle(500, 180)
    # Enter remote mode so we can drive the robot take odometry
    # and attachment motor measurements and program the next steps!

def convert_stick_input(stick):
    if -7 <= stick <= 7:
        return 0
    else:
        return stick / 100


# The main program starts here.
# CODE For FLL/Competitive Robotics. Created
# by team MonongahelaCryptidCooperative
# and Julian Huss
# MonongahelaCryptidCooperative@gmial.com
prime_hub.system.set_stop_button(Button.LEFT in prime_hub.buttons.pressed() and Button.RIGHT in prime_hub.buttons.pressed())
# Override default drivebase settings
drive_base.settings(straight_speed=Straight_Speed)
drive_base.settings(straight_acceleration=Straight_Acceleration)
drive_base.settings(turn_rate=Turn_Speed)
drive_base.settings(turn_acceleration=Turn_Acceleration)
while True:
    # Main loop?GUI that allows for selecting and launching programs
    prime_hub.display.number(Current_Program)
    prime_hub.light.on(Color.GREEN)
    if Button.LEFT in prime_hub.buttons.pressed():
        # Go back to previous program
        Current_Program = Current_Program - 1
        wait(200)
    elif Button.RIGHT in prime_hub.buttons.pressed():
        # Advance to next program
        Current_Program = Current_Program + 1
        wait(200)
    elif Button.CENTER in prime_hub.buttons.pressed():
        if Current_Program == 0:
            pass
        elif Current_Program == 1:
            # Program 1 currently drives in a square
            Square()
        elif Current_Program == 2:
            # Program 2 currently drives in a triangle
            Triangle()
        elif Current_Program == 3:
            # Program 3 currently drives in a circle
            Circle()
        elif Current_Program == 4:
            # Program 4 is an example work in progress,
            # drives forward and moves the attachment
            Example()
        elif Current_Program == 5:
            pass
        else:
            pass
    else:
        wait(5)
