from pybricks.hubs import PrimeHub
from pybricks.iodevices import XboxController
from pybricks.parameters import Axis, Button, Color, Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=-Axis.X, front_side=Axis.Z, broadcast_channel=0, observe_channels=[1])
Left_Color_Sensor = ColorSensor(Port.E)
Right_Color_Sensor = ColorSensor(Port.F)
Left_motor = Motor(Port.A, Direction.COUNTERCLOCKWISE)
Right_Motor = Motor(Port.B, Direction.CLOCKWISE)
drive_base = DriveBase(Left_motor, Right_Motor, 88, 128)
Left_Attachment = Motor(Port.C, Direction.CLOCKWISE)
Right_Attachment = Motor(Port.D, Direction.CLOCKWISE)
XBOX = XboxController()

# Initialize variables.
Current_Program = 1
Straight_Speed = 620
Straight_Acceleration = 500
Turn_Speed = 250
Turn_Acceleration = 500
Attachment_Speed = 810

async def Circle():
    global Current_Program
    await wait(1)
    # Turn button yellow; and beep/wait so hands are out of the way
    prime_hub.light.on(Color.YELLOW)
    await prime_hub.speaker.beep(500, 300)
    # Turn on Gyro, drive forward
    drive_base.use_gyro(True)
    await drive_base.straight(250)
    # Drive in a circle using the curve command
    await drive_base.curve(200, 360)
    # Turn off Gyro and drive/coast back home
    drive_base.use_gyro(False)
    await drive_base.straight(-240, Stop.COAST)
    # Advance to next program automatically
    Current_Program = Current_Program + 1

async def Square():
    global Current_Program
    await wait(1)
    # Turn button yellow; and beep/wait so hands are out of the way
    prime_hub.light.on(Color.YELLOW)
    await prime_hub.speaker.beep(500, 300)
    # Turn on Gyro, drive forward
    drive_base.use_gyro(True)
    await drive_base.straight(150)
    # Drive in a square
    for count in range(4):
        await wait(1)
        await drive_base.straight(300)
        await drive_base.turn(90)
    # Turn off Gyro and drive/coast back home
    drive_base.use_gyro(False)
    await drive_base.straight(-140, Stop.COAST)
    # Advance to next program automatically
    Current_Program = Current_Program + 1

async def Triangle():
    global Current_Program
    await wait(1)
    # Turn button yellow; and beep/wait so hands are out of the way
    prime_hub.light.on(Color.YELLOW)
    await prime_hub.speaker.beep(500, 300)
    # Turn on Gyro, drive forward
    drive_base.use_gyro(True)
    await drive_base.straight(150)
    # Drive in a triangle
    for count2 in range(3):
        await wait(1)
        await drive_base.straight(300)
        await drive_base.turn(120)
    # Turn off Gyro and drive back home
    drive_base.use_gyro(False)
    await drive_base.straight(-140, Stop.COAST)
    # Advance to next program automatically
    Current_Program = Current_Program + 1

async def Example():
    await wait(1)
    # This is an example of an example of a start of an FLL program,
    #  in this example the robot uses a drive forever command to back up
    #  and square up along the rear wall before driving forward.
    #  Lining up and squaring on the rear wall is a good strategy in FLL
    prime_hub.light.on(Color.YELLOW)
    drive_base.drive(-100, 0)
    await prime_hub.speaker.beep(500, 300)
    drive_base.stop()
    await wait(200)
    # Turn on Gyro after squaring off the back wall then
    #  drive forward and move left attachment
    drive_base.use_gyro(True)
    await drive_base.straight(150)
    await Left_Attachment.run_angle(500, 180)
    # Enter remote mode so we can teleop the robot/take odometry
    # and attachment motor measurements to program the next steps!
    await remote_mode()

async def remote_mode():
    await wait(1)
    # Zero out the odometry and attachment motors so we can
    # take measurements
    drive_base.reset()
    Left_Attachment.reset_angle(0)
    Right_Attachment.reset_angle(0)
    # Overide drivebase settings. In RC mode it is okay to have higher
    # acceleration values (drives smoother, accuracy does not matter as much), but may want lower speeds to make driving easier,
    # Adjust as you see fit. Turn on Gyro
    drive_base.settings(straight_speed=300)
    drive_base.settings(straight_acceleration=1000)
    drive_base.settings(turn_rate=200)
    drive_base.settings(turn_acceleration=1000)
    drive_base.use_gyro(True)
    while True:
        await wait(1)
        prime_hub.light.on(Color.RED)
        if Button.A in XBOX.buttons.pressed():
            # If the A button is pressed print off to the console:
            # how far the robot drove,
            # how much it turned,
            # how far the attachment motors were moved
            # then reset all of these measurements so new ones
            # can be taken
            print(drive_base.distance(), ' mm driven')
            print(drive_base.angle(), ' degrees turned')
            print(Left_Attachment.angle(), ' degrees left attachment')
            print(Right_Attachment.angle(), ' degrees right attachment')
            Left_Attachment.reset_angle(0)
            Right_Attachment.reset_angle(0)
            drive_base.reset()
            # Beep the robot and rumble the xbox controller
            #  to let it known that a measurement was taken
            await multitask(prime_hub.speaker.beep(500, 400), XBOX.rumble(50, 400))
        elif Button.X in XBOX.buttons.pressed():
            # X exits RC mode and allows a new program to be selected
            # Reset drivebase back to where it was
            drive_base.settings(straight_speed=Straight_Speed)
            drive_base.settings(straight_acceleration=Straight_Acceleration)
            drive_base.settings(turn_rate=Turn_Speed)
            drive_base.settings(turn_acceleration=Turn_Acceleration)
            drive_base.use_gyro(False)
            await multitask(prime_hub.speaker.beep(700, 400), XBOX.rumble(50, 400))
            break
        else:
            # Figure out forward/reverse and turning controller inputs
            # Foward/reverse: Left stick up/down
            # Turning: Right stick left/right
            drive_base.drive(await convert_stick_input(XBOX.joystick_left()[1]) * Straight_Speed, await convert_stick_input(XBOX.joystick_right()[0]) * Turn_Speed)
            if Button.RB in XBOX.buttons.pressed():
                # Control the Right attachment motor
                # Right trigger = right motor forward
                # Right trigger + right bumper  = right motor backwards
                Right_Attachment.run(await convert_stick_input(XBOX.triggers()[1]) * (Attachment_Speed * -1))
            else:
                Right_Attachment.run(await convert_stick_input(XBOX.triggers()[1]) * Attachment_Speed)
            if Button.LB in XBOX.buttons.pressed():
                # Control the Left attachment motor
                # Left trigger = left motor forward
                # Left trigger + Left bumper  = Left motor backwards
                Left_Attachment.run(await convert_stick_input(XBOX.triggers()[0]) * (Attachment_Speed * -1))
            else:
                Left_Attachment.run(await convert_stick_input(XBOX.triggers()[0]) * Attachment_Speed)
        await wait(10)

async def convert_stick_input(stick):
    await wait(1)
    # Convert the input from the xbox controller
    # from -100 to 100 to -1 to 1. Also change small
    # values to 0 to eliminate stick drift. Rescales the
    # inputs appropriately
    if -7 <= stick <= 7:
        # Return 0 to eliminate  stick drift
        return 0
    elif 7 > stick:
        # Rescale positive (right turning input) given that 0  to  7 = 0
        return (stick - 7) / 93
    else:
        # Rescale negative (left turning input) given that 0 to -7 = 0
        return (stick + 7) / 93

async def main():
    global Current_Program
    # Code For FLL/Competitive Robotics.
    # Created  by FTC team 23247
    # MonongahelaCryptidCooperative
    # and Julian Huss
    # MonongahelaCryptidCooperative@gmail.com
    # https://www.facebook.com/groups/8136523133029961
    # https://github.com/MonongahelaCryptidCooperative
    # Free to use but do credit us/give us a shout out!
    # To use this code you will have to change the setup blocks to fit your
    # Robot's configuration. You will likely have a different hub orietation
    # as the hub in this code is oriented vertically facing backward
    # (very atypical for FLL).

    # For example the commonly used Advanced Driving Base should be as follows:
    # Topside = z axis, Frontside = y axis
    # The very good Droid Bot E (from FLL tutorials) would be:
    # Topside = z axis, Frontside = - x axis
    # Look into the "Reference Frames" section of Pybricks documenation
    # to figure out other designs

    # You will also need to change the wheel diameter to fit your design.
    # The large wheels in the spike kit are 88 mm, the medium wheels are 56mm
    # Wheel spacing is figured out my measuring the CONTACT POINT between
    # the two wheels. You could also measure from the inside to the ouside
    #  of your wheels as this should be the same as the contact points.
    # Technic holes are 8 mm apart so if you don't have a metric ruler handy
    #  you can always count holes as well.
    # First make it so the robot does not stop when center button is pressed
    # This code assumes you want to use an XBOX controller. If an XBOX controller
    # is not paired in 30 seconds it will crash.
    #  If you do not want to use the XBOX  controller you can just disconnect
    #  the "XBOX" block at the bottom of the startup section
    #  or use the BlockStarterPack.py code

    # The XBOX RC mode is useful to "teleop" the robot to try out different
    # strategies/attachments, etc. and to take measurements to use in programming
    prime_hub.system.set_stop_button(None)
    # Override default drivebase settings. The default pybricks settings
    #  tend to have too little max speed (slower than necessary)
    #  and too high of an acceleration (wheels slip/inacurate movements)

    # Spike prime motors (medium/large are maximally efficient at
    # 135 rpms. Pybricks defines speeds in mm/s.
    # You can calculate max speed as follows:

    # 135 * wheel-diameter (mm) * pi / 60 (seconds)

    # For the large wheels (88 mm)  in the spike set this is about 621 mm/s

    # Turning speed will vary with the width of the robot and is in degrees/s
    # 200-400 is reasonable

    # Play around with acceleration values. You want values high enough to
    # accelerate well but low enough that the wheels do not slip. By default pybricks
    # chooses values around 1000. We find this often is too agressive.
    #  Values will depend on center of gravity, traction, weight of the robot etc.
    # You may want to write functions (not provided here) to change accelleration
    #  on the  fly if you have an attachment style robot and attachments that are
    # particularly heavy/unbalanced or if you are needing more accurate
    # movements in certain situations
    drive_base.settings(straight_speed=Straight_Speed)
    drive_base.settings(straight_acceleration=Straight_Acceleration)
    drive_base.settings(turn_rate=Turn_Speed)
    drive_base.settings(turn_acceleration=Turn_Acceleration)
    # Main loop/interface that allows for selecting and launching programs
    while True:
        await wait(1)
        prime_hub.display.number(Current_Program)
        prime_hub.light.on(Color.GREEN)
        if Button.LEFT in prime_hub.buttons.pressed():
            # The left button goes back to the previous program
            Current_Program = Current_Program - 1
            await wait(200)
        elif Button.RIGHT in prime_hub.buttons.pressed():
            # The right button advances to the next program
            Current_Program = Current_Program + 1
            await wait(200)
        elif Button.CENTER in prime_hub.buttons.pressed():
            # The center button launches the currently selected program
            # Note you can also have this be the bluetooth button which is what
            # our FLL team did due to our unique robot design obstructing the
            # center button
            if Current_Program == 0:
                # Program 00, currently enters remote mode
                # Given that the robot starts at 0 1 you will have
                # to go back one program to select this
                await remote_mode()
            elif True:
                # Program  01 currently drives in a square
                # Replace with your own program.
                await Square()
            elif Current_Program == 2:
                # Program 02 currently drives in a triangle
                # Replace with your own program.
                await Triangle()
            elif Current_Program == 3:
                # Program 03 currently drives in a circle
                # Replace with your own program.
                await Circle()
            elif Current_Program == 4:
                # Program 04 is an example work in progress,
                # drives forward and enters remote mode
                # to allow for manual control to
                # obtain more measuremnts for programming
                # This is how our team programmed our robot  in FLL
                await Example()
            elif Current_Program == 5:
                # Empty program 05, can add as many additional
                # conditionals/programs as needed. Pybricks
                # can display the numbers between -99, 99 so
                # this UI allows for a max of 199 entries.
                # This is far more than the official spike software
                pass
            else:
                pass
        else:
            await wait(10)


